import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import PatchFrom from './PatchForm';

const MemeDetails = () => {
  //const [name,setName] = useState('default name');
  const [datez,setDate] = useState('default date');
  const {id} = useParams();

  const url = 'http://localhost:8000/memes/'
  const [details,setDetails] = useState([]);
  const fetchDetails = async () => {
    try{
      const response = await fetch(url+id);
      const details = await response.json();
      setDetails(details);
      setDate(details.date);
      console.log(datez);
    } catch (error){
      console.log(error);
    }
  };

  useEffect(() => {
    fetchDetails();
  },[]);

  return (
    <main>
      <article className="single-meme single-meme-details">
        <PatchFrom {...details}/>
        <div className="title">
            <h2>Meme Details</h2>
            <div className="underline"></div>
        </div>
        <header>
          <div className="meme-info">
            <h4 className="meme-details">{details.name}</h4>
            <Link to="/" className='btn'>
              Back to Memes
            </Link>
          </div>
          <div className="meme-info">
              <p className="meme-details">{datez.toLocaleString()}</p>
          </div>
          <p>{details.caption}</p>
        </header>
        <img src={details.image_file} alt={details.name}/>

      </article>
    </main>
  );
};

export default MemeDetails;
