import React, { useState } from 'react';
import axios from 'axios';

const PatchFrom = ( {id} ) => {
    const [caption,setCaption] = useState('');
    const [imageUrl,setImageUrl] = useState('');

    const url = 'http://localhost:8000/memes/'

    function wait(ms){
        var start = new Date().getTime();
        var end = start;
        while(end < start + ms){
            end = new Date().getTime();
        }
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        const payload = {
            "caption":caption,
            "image_url":imageUrl
        };
        console.log(payload);

        axios.patch(url+id,payload)
            .then(response => {
                console.log(response);
            })
            .catch(error => {
                console.log(error);
            });
        //window.location.href = "/memes/"+id;
        wait(1000);
        window.location.reload(false);
    }

    return (
        <section className="section-center">
            <div className="title">
                <h2>Patch Meme</h2>
                <div className="underline"></div>
            </div>
            <form onSubmit={handleSubmit}>
                <div>
                    Caption : <input type="text" name="caption" value={caption} onChange={(e) => setCaption(e.target.value)}/>
                </div>
                <div>
                    Meme URL : <input type="text" name="imageUrl" value={imageUrl} onChange={(e) => setImageUrl(e.target.value)}/>
                </div>
                <button type="submit" className="btn">Submit</button>
            </form>
        </section>
    );
};

export default PatchFrom;
