import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const PostFrom = () => {
    const [name,setName] = useState('default name');
    const [caption,setCaption] = useState('default caption');
    const [imageUrl,setImageUrl] = useState('default imageUrl');

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(e);
    }

    return (
        <section className="section-center">
            <form onSubmit={handleSubmit}>
                <div>
                    <input type="text" name="name" value={name} onChange={(e) => setName(e.target.value)}/>
                </div>
                <div>
                    <input type="text" name="caption" value={caption} onChange={(e) => setCaption(e.target.value)}/>
                </div>
                <div>
                    <input type="text" name="imageUrl" value={imageUrl} onChange={(e) => setImageUrl(e.target.value)}/>
                </div>
                <button type="submit" className="btn">Submit</button>
            </form>
        </section>
    );
};

export default PostFrom;
