import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const PostFrom = () => {
    const [name,setName] = useState('');
    const [caption,setCaption] = useState('');
    const [imageUrl,setImageUrl] = useState('');

    const url = 'http://localhost:8000/memes/'
    const handleSubmit = (e) => {
        e.preventDefault();
        const payload = {
            "name":name,
            "caption":caption,
            "image_url":imageUrl
        };
        console.log(payload);
        axios.post(url,payload)
            .then(response => {
                console.log(response);
            })
            .catch(error => {
                console.log(error);
            })
    }

    return (
        <section className="section-center">
            <div className="title">
                <h2>Post Memes</h2>
                <div className="underline"></div>
            </div>
            <form onSubmit={handleSubmit}>
                <div>
                    Name : <input type="text" name="name" value={name} onChange={(e) => setName(e.target.value)}/>
                </div>
                <div>
                    Caption : <input type="text" name="caption" value={caption} onChange={(e) => setCaption(e.target.value)}/>
                </div>
                <div>
                    Meme URL : <input type="text" name="imageUrl" value={imageUrl} onChange={(e) => setImageUrl(e.target.value)}/>
                </div>
                <button type="submit" className="btn">Submit</button>
            </form>
        </section>
    );
};

export default PostFrom;
