import React, { useState } from 'react';
import axios from 'axios';
import { useHistory, Link } from 'react-router-dom';

const PatchFrom = ( {id} ) => {
    const [caption,setCaption] = useState('');
    const [imageUrl,setImageUrl] = useState('');
    const history = useHistory();

    const handleCustom = () => {
        console.log("/memes/"+id);
        //history.push(`/memes/${id}`);
        history.go();
    }

    const url = 'http://localhost:8000/memes/'
    const handleSubmit = (e) => {
        e.preventDefault();
        const payload = {
            "caption":caption,
            "image_url":imageUrl
        };
        console.log(payload);

        axios.patch(url+id,payload)
            .then(response => {
                console.log(response);
            })
            .catch(error => {
                console.log(error);
            });

        //history.push("/memes/"+id);
        //history.push("/");

        setTimeout(function(){
            console.log("ihii");
            history.go(0);
        },1000);
        /*
        history.go(0);
        handleCustom();
        */
    }

    return (
        <section className="section-center">
            <div className="title">
                <h2>Patch Meme</h2>
                <div className="underline"></div>
            </div>
            <form onSubmit={handleSubmit} className="smaller-input">
                <div>
                    Caption <br></br> <input type="text" name="caption" value={caption} onChange={(e) => setCaption(e.target.value)}/>
                </div>
                <div>
                    Meme URL <br></br> <input type="text" name="imageUrl" value={imageUrl} onChange={(e) => setImageUrl(e.target.value)}/>
                </div>
                <button type="submit" className="btn" >Submit</button>
            </form>
            <button className="btn" onClick={handleCustom}>click</button>
            <Link to={`/memes/${id}`}>
                click
            </Link>
        </section>
    );
};

export default PatchFrom;
