import React, { useState } from 'react';
import axios from 'axios';

const PatchFrom = ( {id,caption,image_url} ) => {
    /*
    const [caption,setCaption] = useState('');
    const [imageUrl,setImageUrl] = useState('');
    */
    //return (<h4> PatchFrom page {id} {caption} {image_url} </h4>);

    const url = 'http://localhost:8000/memes/'
    const handleSubmit = (e) => {
        e.preventDefault();
        const payload = {
            "caption":caption,
            "image_url":imageUrl
        };
        console.log(payload);
        /*
        axios.post(url,payload)
            .then(response => {
                console.log(response);
            })
            .catch(error => {
                console.log(error);
            })
        */
    }
    const changeHandler = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }

    return (
        <section className="section-center">
            <div className="title">
                <h2>Patch Meme</h2>
                <div className="underline"></div>
            </div>
            <form onSubmit={handleSubmit}>
                <div>
                    Caption : <input type="text" name="caption" value={caption} onChange={this.changeHandler}/>
                </div>
                <div>
                    Meme URL : <input type="text" name="imageUrl" value={image_url} onChange={this.changeHandler}/>
                </div>
                <button type="submit" className="btn">Submit</button>
            </form>
        </section>
    );
};

export default PatchFrom;
