import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';

const MemeDetails = () => {
  const [name,setName] = useState('default name');
  const {id} = useParams();

  const url = 'http://localhost:8000/memes/'
  const [details,setDetails] = useState([]);
  const fetchDetails = async () => {
    try{
      const response = await fetch(url+id);
      const details = await response.json();
      setDetails(details);
      const datez = new Date(details.date);
    } catch (error){
      console.log(error);
    }
  };

  useEffect(() => {
    fetchDetails();
  },[]);

  return (<article className="single-meme">
    <header>
      <div className="meme-info">
        <h4 className="meme-details">{details.name}</h4>
      </div>
      <div className="meme-info">
          <p className="meme-details">{datez.toLocaleString()}</p>
      </div>
      <p>{details.caption}</p>
    </header>
    <img src={details.image_file} alt={name}/>
    <Link to="/" className='btn'>
        Back to Memes
    </Link>

  </article>);
};

export default MemeDetails;
